mapper.select:
select * from (select p.* ,count( case isCheck when 1 then 1 else null end)+1 nowMemberCount from (select t.teamId,t.teamName,teamIcon,teamCount,startTime,endTime,location,needing,additionNeading,pics,leader,state,type1,type2, timestampdiff(day,now(),endTime) as leaveDay, mod(timestampdiff(hour,now(),endTime),24) as leaveHour, mod(timestampdiff(minute,now(),endTime),24) as leaveMinus, isCheck from teamInfo t left join teamMenber m on t.teamId=m.teamId where
state = #{state} and
state < 2 and
endTime > now()
and leader = #{leader ,jdbcType=VARCHAR}
and t.teamId = #{teamId,jdbcType=VARCHAR}
) p group by p.teamId ) g where g.teamId is not null order by g.teamId desc limit #{start},#{pageSize}

select *
from teamMenber
where
teamId=#{teamId,jdbcType=INTEGER}

	and menber = #{menber,jdbcType=VARCHAR}


	and isCheck = #{isCheck}

mapper.update:
update  teamInfo
set state=3
where teamId=#{teamId} and leader=#{leader,jdbcType=INTEGER}

update teamMenber
set isCheck=#{isCheck}
where teamId=#{teamId} and menber=#{menber,jdbcType=VARCHAR}

mapper.select:
select * from ( select p.* ,count( case isCheck when 1 then 1 else null end)+1 nowMemberCount from (select distinct t.teamId as teamId,t.teamName as teamName, teamCount,startTime,endTime,location,needing,additionNeading,leader,state,type1,type2,teamIcon,pics,isCheck from teamInfo t left join teamMenber m on t.teamId=m.teamId where
state = #{state} and
(t.leader=#{leader,jdbcType=VARCHAR} or (m.menber=#{leader,jdbcType=VARCHAR} and m.isCheck=1) ) )p group by p.teamId ) g where teamId is not null order by teamId desc limit #{start},#{pageSize}

select  teamId
from teamInfo
where
leader=#{leader,jdbcType=VARCHAR}
and startTime=#{startTime,jdbcType=VARCHAR}
and endTime=#{endTime,jdbcType=VARCHAR}

	and teamName = #{teamName,jdbcType=VARCHAR}


	and needing = #{needing,jdbcType=VARCHAR}


	and additionNeading = #{additionNeading,jdbcType=VARCHAR}

mapper.update:
update teamMenber
set isCheck=3
where teamId=#{teamId} and isCheck=2;
